import React from "react";
import * as userServices from "../../services/userServices";
import SingleFriend from "./SingleFriend";

class Friends extends React.Component {
  state = { friends: [{ title: "", summary: "", photoImage: "" }] };

  componentDidMount() {
    userServices
      .getFriends(0, 3)
      .then(this.onGetSuccess)
      .catch(this.onGetError);
  }

  onGetSuccess = (myFriends) => {
    let frie = myFriends.data.item.pagedItems;
    console.log(myFriends.data.item.pagedItems);
    this.setState((preState) => {
      return { mappedFriends: frie.map(this.mapFriend) };
    });
  };
  onGetError = (error) => {
    console.log(error);
  };

  onEditClickFull = (friend) => {
    let ticks = friend.id;
    console.log(friend.id);
    this.props.history.push(`/friend/${ticks}/edit`);
  };

  onDeleteClickFull = (friend) => {
    console.log(friend);
    userServices
      .deleteFriendsId(friend.id)
      .then(this.onDeleteSuccess)
      .catch(this.onDeleteError);
  };
  onDeleteSuccess = (friend) => {
    console.log("onDelete", { friend: friend });

    this.setState((prevState) => {
      const indexOfPerson = prevState.friends.findIndex(
        (singleArrayMemeber) => singleArrayMemeber.id === friend.id
      );
      const updatedPeople = [...prevState.friends];
      if (indexOfPerson >= 0) {
        updatedPeople.splice(indexOfPerson, 1);
      }
      return { people: updatedPeople, FormData: null };
    }, this.stateChanged);
  };
  onDeleteError = (error) => {
    console.log(error);
  };

  mapFriend = (oneFriend) => {
    return (
      <SingleFriend
        friend={oneFriend}
        onDelete={this.onDeleteClickFull}
        onEdit={this.onEditClickFull}
        key={oneFriend.id}
      ></SingleFriend>
    );
  };
  render() {
    return (
      <div className="col-md-12 p-5">
        <h1>Friends</h1>
        <hr />
        <div className="row">{this.state.mappedFriends}</div>
      </div>
    );
  }
}
export default Friends;
