import React from "react";
import * as userServices from "../../services/userServices";
import SingleFriend from "./SingleFriend";

class Friends extends React.Component {
  state = { friends: [{ title: "", summary: "", photoImage: "" }] };

  componentDidMount() {
    userServices
      .getFriends(0, 3)
      .then(this.onGetSuccess)
      .catch(this.onGetError);
  }

  onGetSuccess = (myFriends) => {
    let frie = myFriends.data.item.pagedItems;
    console.log(myFriends.data.item.pagedItems);
    this.setState((preState) => {
      return { mappedFriends: frie.map(this.mapFriend) };
    });
  };
  onGetError = (error) => {
    console.log(error);
  };

  onEditClick = (e) => {
    console.log(e.currentTarget.dataset.friendId);
  };
  onEditClick = (friend) => {
    console.log(friend);
  };
  onDeleteClick = (e) => {
    console.log(e.currentTarget.dataset.friendId);
  };
  onDeleteClickFull = (friend) => {
    console.log(friend);
  };
  mapFriend = (oneFriend) => {
    return (
      <div className="card col-md-3" key={oneFriend.id}>
        <img src={oneFriend.photoImage} className="card-img-top" alt="..." />
        <div className="card-body">
          <h5 className="card-title">{oneFriend.title}</h5>
          <p className="card-text">{oneFriend.summary}</p>
          <div className="d-grid gap-2 d-md-flex">
            <button
              className="btn  btn-danger"
              type="button"
              onClick={this.onDeleteClickFull}
              data-friend-id={oneFriend.id}
            >
              Delete
            </button>
            <button
              className="btn btn-primary"
              type="button"
              onClick={this.onEditClickFull}
              data-friend-id={oneFriend.id}
            >
              Edit
            </button>
          </div>
        </div>
      </div>
    );
  };

  // mapFriendSimple = (oneFriend) => {
  //   return <p key={`,myFriends-${oneFriend.id}`}>{oneFriend.title}</p>;
  // };
  render() {
    return (
      <div className="col-md-12 p-5">
        <h1>Friends</h1>
        <hr />
        <div className="row">{this.state.mappedFriends}</div>
      </div>
    );
  }
}
export default Friends;
