import React from "react";
import * as seperate from "./seperate";
import { toast } from "react-toastify";

toast.configure();
class Widget extends React.Component {
  state = {
    FormData: {
      name: "",
      manufacturer: "",
      description: "",
      cost: 0,
    },
  };

  onButtonClicked = (e) => {
    e.preventDefault();
    console.log("I was clicked");

    seperate.addEntity.then(this.onActionSuccess).catch(this.onActionError);
  };

  onActionSuccess = (response) => {
    console.log(response);
    toast.success("Registration Success");
  };
  onActionError = (err) => {
    console.log(err);
    toast.error("Registration failed ");
  };
  render() {
    return (
      <main role="main">
        <div className="container border border-secondary border-3">
          <div className="row row-cols-lg-auto g-3 align-items-center">
            <div className="col-lg">
              <form>
                <h1 className="display-7 fw-bold text-center">Information</h1>
                <div className="mb-3">
                  <label
                    htmlFor="exampleInputPassword1"
                    className="form-label"
                  ></label>
                  <input
                    type="text"
                    className="form-control"
                    placeholder=" Name"
                  />
                </div>
                <div className="mb-3">
                  <label
                    htmlFor="exampleInputPassword1"
                    className="form-label"
                  ></label>
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Manufacturer"
                  />
                </div>
                <div className="mb-3">
                  <label
                    htmlFor="exampleInputPassword1"
                    className="form-label"
                  ></label>
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Description"
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label"></label>
                  <input
                    type="number"
                    className="form-control"
                    placeholder="cost"
                  />
                </div>

                <button
                  type="submit"
                  className="btn btn-primary"
                  onClick={this.onButtonClicked}
                >
                  Submit
                </button>
              </form>
            </div>
          </div>
        </div>
      </main>
    );
  }
}

export default Widget;
